import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './spinner';
import PropTypes from 'prop-types'


export class News extends Component {
static(){
  
}
constructor(){
    super();
    // console.log("I am a constructor from News Components")
    this.state = {
        articles : [],
        loading : false,
        page : 1
    }
}



async componentDidMount(){
  // console.log("cdm")
  let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=business&apiKey=2ef83903e6c04587a1f923641fbfc546&page=1&pageSize=${this.props.pageSize}`;
  this.setState({loading: true})
  let data = await fetch(url)
  let parsedData = await data.json()
  // console.log(parsedData);
  this.setState({
    articles: parsedData.articles,
    totalResults : parsedData.totalResults,
    loading: false
  })
};



  HandlePrevClick= async () => {
  console.log("Previous")
  let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=business&apiKey=2ef83903e6c04587a1f923641fbfc546&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
  this.setState({loading: true})
  let data = await fetch(url)
  let parsedData = await data.json()
  // console.log(parsedData);

  this.setState({
    page: this.state.page - 1,
    articles: parsedData.articles,
    loading: false
  })}



  HandleNextClick= async () => {
  console.log("Next")
  if (!(this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize))){

  let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=business&apiKey=2ef83903e6c04587a1f923641fbfc546&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
  this.setState({loading: true})
  let data = await fetch(url)
  let parsedData = await data.json()
  // console.log(parsedData);

  this.setState({
    page: this.state.page+1,
    articles: parsedData.articles,
   loading: false
  })}}




  render() {
    // console.log("render")
    return (
      <div className='container my-3'>
        <h2 className='d-flex justify-content-center'>NewsMonkey - Top Headlines</h2>
        {this.state.loading && <Spinner/>}
        <div className="row">
        {!this.state.loading && this.state.articles.map((element)=>{
         return <div className="col-md-4 my-1"  key={element.url}>
      <NewsItem title={element.title} description= {element.description} imageurl={element.urlToImage} newsURL={element.url}/>
      </div>
      })}
      </div>
      <div className="container d-flex justify-content-between">
      <button disabled = {this.state.page<=1} type="button" className="btn btn-dark" onClick={this.HandlePrevClick}>&larr; Previous</button>
      <button disabled ={this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.HandleNextClick}>&rarr; Next</button>
      </div>
      </div>
    )
  }
}

export default News
