import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './spinner';
import PropTypes from 'prop-types'


export class News extends Component {
  static defaultProps = {
    country: 'in',
    pageSize: 8,
    category : 'general'
    
  };
  
  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category : PropTypes.string
  };
 
  capitalizeFirstLetter=(string)=>{
return string.charAt(0).toUpperCase() + string.slice(1);
  }
  
constructor(props){
    super(props);
    // console.log("I am a constructor from News Components")
    this.state = {
        articles : [],
        loading : false,
        page : 1,
    }
    document.title = `${this.capitalizeFirstLetter(this.props.category)} - NewsMonkey`;

}

async Updatenews(){
  const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=2ef83903e6c04587a1f923641fbfc546&page=${this.state.page}&pageSize=${this.props.pageSize}`;
  this.setState({loading: true})
  let data = await fetch(url)
  let parsedData = await data.json()
  // console.log(parsedData);
  this.setState({
    articles: parsedData.articles,
    totalResults : parsedData.totalResults,
    loading: false
  })
}

async componentDidMount(){
  // console.log("cdm")
this.Updatenews();
};





  HandlePrevClick= async () => {
  // console.log("Previous")
  // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=2ef83903e6c04587a1f923641fbfc546&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
  // this.setState({loading: true})
  // let data = await fetch(url)
  // let parsedData = await data.json()
  // // console.log(parsedData);

  // this.setState({
  //   page: this.state.page - 1,
  //   articles: parsedData.articles,
  //   loading: false
  // })
  this.setState({page:this.state.page-1});
  this.Updatenews()

}



  HandleNextClick= async () => {
  // console.log("Next")
  // if (!(this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize))){

  // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=2ef83903e6c04587a1f923641fbfc546&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
  // this.setState({loading: true})
  // let data = await fetch(url)
  // let parsedData = await data.json()
  // // console.log(parsedData);

  // this.setState({
  //   page: this.state.page+1,
  //   articles: parsedData.articles,
  //  loading: false
  // })}
  this.setState({page:this.state.page+1});
  this.Updatenews()
 
}




  render() {
    return (
      <div className='container my-3'>
        <h2 className='d-flex justify-content-center tochangeheight'>NewsMonkey - Top Headlines on  ${this.capitalizeFirstLetter(this.props.category)}`</h2>
        {this.state.loading && <Spinner />}
        <div className="row">
          {Array.isArray(this.state.articles) && this.state.articles.map((element) => {
            return (
              <div className="col-md-4 my-1" key={element.url} >
                <NewsItem title={element.title} description={element.description} imageurl={element.urlToImage} newsURL={element.url} date={element.publishedAt} author={element.author}  source={element.source.name}/>
              </div>
            );
          })}
          
      </div>
      <div className="container d-flex justify-content-between">
      <button disabled = {this.state.page<=1} type="button" className="btn btn-dark" onClick={this.HandlePrevClick}>&larr; Previous</button>
      <button disabled ={this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.HandleNextClick}>&rarr; Next</button>
      </div>
      </div>
    )
  }
}


export default News
