import React, { Component } from 'react'
import NewsItem from './NewsItem'


export class News extends Component {

constructor(){
    super();
    // console.log("I am a constructor from News Components")
    this.state = {
        articles : [],
        loading : false,
        page : 1
    }
}


HandlePrevClick=() => {

}

HandleNextClick= () => {

}


async componentDidMount(){
  console.log("cdm")
  let url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=2ef83903e6c04587a1f923641fbfc546&page=1";
  let data = await fetch(url)
  let parsedData = await data.json()
  // console.log(parsedData);
  this.setState({articles: parsedData.articles})
};

  render() {
    console.log("render")
    return (
      <div className='container my-3 ' >
        <h2>NewsMonkey - Top Headlines</h2>
        <div className="row">
        {this.state.articles.map((element)=>{
         return <div className="col-md-4 my-1"  key={element.url}>
      <NewsItem title={element.title} description= {element.description} imageurl={element.urlToImage} newsURL={element.url}/>
      </div>
      })}
      </div>
      <div className="container d-flex justify-content-between">
      <button type="button" class="btn btn-dark" onClick={HandlePrevClick}>Previous</button>
      <button type="button" class="btn btn-dark" onClick={HandleNextClick}>Next</button>
      </div>
      </div>
    )
  }
}

export default News
