import React, { Component } from 'react'


export class NewsItem extends Component {
  render(){
   let {title, description, imageurl, newsURL, date, author} = this.props;
  
    return (
      <div className='my-3'>
        <div className="card" >
        <span class="position-absolute top-0 start- translate-middle badge rounded-pill bg-danger">
    99+
    <span class="visually-hidden">unread messages</span>
  </span>
    <img className="card-img-top" src={!imageurl?"https://static.vecteezy.com/system/resources/thumbnails/006/299/370/original/world-breaking-news-digital-earth-hud-rotating-globe-rotating-free-video.jpg":imageurl} alt="Card image cap"/>
    <div className="card-body">
        <h5 className="card-title">{!title?"Undefined Title":title}</h5>
        <p className="card-text">{!description?"Undefined description":description}</p>
        <h6 className="card-title">Author:</h6><p>{ !author?"Undefined Author":author}</p>
        <h6 className="card-title">Published At:</h6><p>{!date?"Undefind Date":new Date (date).toUTCString()}</p>   
        <a href={newsURL} rel="noreferrer" target="_blank" className="btn btn-sm btn-dark">Read More</a>
    </div>
    </div>
      </div>
    )
  }
}
export default NewsItem;
