import React, {useState} from 'react'

export default function TextForm(props) {
    const handleUpClick = ()=>{
        // console.log("UpperCase was Clicked"+ text)
        let newText = text.toUpperCase();
        setText(newText)
    }
  
    const handleDownClick = ()=>{
        // console.log("UpperCase was Clicked"+ text)
        let newText = text.toLowerCase();
        setText(newText)
    }

    const handleOnChange = (event)=>{
    //     console.log("On change")
        setText(event.target.value)
    }

    const handleClearClick = () =>{
        let newText = ""
        setText(newText)
    }

    const handlefirstClick = () =>{
        let newText = text.split(' ');
        for (let i=0; i < newText.length; i++){
            newText[i] = newText[i].charAt(0).toUpperCase + newText[i].slice(1).toLocaleLowerCase
            let newText = newText.join(' ')
            setText(newText)
        }
    }

    // const handlefirstClick = () => {
    //     let words = text.split(' ');
    //     for (let i = 0; i < words.length; i++) {
    //         words[i] = words[i].charAt(0).toUpperCase() + words[i].slice(1).toLowerCase();
    //     }
    //     let newText = words.join(' ');
    //     setText(newText);
    // }
    

    const[text, setText] = useState("");
    // setText("Enter Text here2");
    return (
        <>
            <div className='container'>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                
                <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
                </div>
                
                
                <button className="button btn btn-primary mx-1" onClick={handleUpClick}>Convert To UpperCase</button>        
                <button className="button btn btn-primary mx-1" onClick={handleDownClick}>Convert To LowerCase</button>    
                <button className="button btn btn-primary mx-1" onClick={handleClearClick}>Clear Text</button>    
                <button className="button btn btn-primary mx-1" onClick={handlefirstClick}>Convert 1st Letter to Up.Case</button>    
                    

            </div>
            <div className="container my-3">
                <h2>Your text Summary</h2>
                <p>{text.split(" ").length} Words, {text.length} Characters and {text.split(". ").length -1 } Sentences</p>
                <p>{0.008 * text.split(" ").length} Minutes Read</p>
                <h2>Preview</h2>
                <p>{text}</p>
            </div>
            <div className="container my-2">
        <h2>Your Sentences Length Here</h2>
        <p></p>
            </div>
            </>
    )
}
