import React, {useState} from 'react'



export default function TextForm(props) {
const[text, setText] = useState("Enter Text here");
  return (
        <div>
            <div className="mb-3">
            <label htmlFor="myBox" className="form-label" value={text} >{props.heading}</label>
            <textarea className="form-control" value={text} id="myBox" rows="10"></textarea>
            </div>
            <div className="button btn btn-primary">Convert To UpperCase</div>        
        </div>
  )
}
